import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null); // Menyimpan user yang sedang diedit
  const [formData, setFormData] = useState({ name: "", email: "", contact: "" }); // Menyimpan data form
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8000/server/index.php")
      .then((response) => response.json())
      .then((data) => {
        setUsers(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const deleteUser = (id) => {
    fetch(`http://localhost:8000/server/index.php?id=${id}`, {
      method: "DELETE",
    })
      .then(() => {
        setUsers(users.filter((user) => user.id !== id));
      })
      .catch((error) => {
        console.error("Error deleting user:", error);
      });
  };

  const editUser = (user) => {
    setEditingUser(user);
    setFormData({ name: user.name, email: user.email, contact: user.contact });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const method = editingUser ? "PUT" : "POST";
    const url = editingUser
      ? `http://localhost:8000/server/index.php?id=${editingUser.id}`
      : "http://localhost:8000/server/index.php";

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then(() => {
        if (editingUser) {
          // Jika mengedit, update data di state
          setUsers(
            users.map((user) =>
              user.id === editingUser.id ? { ...user, ...formData } : user
            )
          );
        } else {
          // Jika menambah, tambahkan data baru
          setUsers([...users, { ...formData, id: users.length + 1 }]);
        }
        setEditingUser(null); // Reset form dan hilangkan mode edit
        setFormData({ name: "", email: "", contact: "" });
      })
      .catch((error) => {
        console.error("Error saving user:", error);
      });
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Users</h1>
      <button
        className="bg-green-500 text-white px-4 py-2 rounded mb-4"
        onClick={() => setEditingUser(null)} // Reset form untuk menambah user baru
      >
        Add User
      </button>

      {/* Form Edit / Add */}
      {(editingUser || !editingUser) && (
        <form onSubmit={handleFormSubmit} className="mb-6">
          <h2 className="text-xl font-semibold mb-4">
            {editingUser ? "Edit User" : "Add User"}
          </h2>
          <div className="mb-4">
            <label className="block text-gray-700">Name</label>
            <input
              type="text"
              className="border border-gray-300 p-2 w-full"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Email</label>
            <input
              type="email"
              className="border border-gray-300 p-2 w-full"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Contact</label>
            <input
              type="text"
              className="border border-gray-300 p-2 w-full"
              value={formData.contact}
              onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            {editingUser ? "Update" : "Add"}
          </button>
        </form>
      )}

      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr>
            <th className="border border-gray-300 px-4 py-2">Name</th>
            <th className="border border-gray-300 px-4 py-2">Email</th>
            <th className="border border-gray-300 px-4 py-2">Contact</th>
            <th className="border border-gray-300 px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td className="border border-gray-300 px-4 py-2">{user.name}</td>
              <td className="border border-gray-300 px-4 py-2">{user.email}</td>
              <td className="border border-gray-300 px-4 py-2">{user.contact}</td>
              <td className="border border-gray-300 px-4 py-2">
                <button
                  className="bg-blue-500 text-white px-4 py-2 rounded mr-2"
                  onClick={() => editUser(user)} // Set user for editing
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-4 py-2 rounded"
                  onClick={() => deleteUser(user.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
