import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AddHome = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const navigate = useNavigate();

  const saveData = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost/api/AddHome.php", {
        name,
        email,
        contact,
      });
      alert("Data berhasil ditambahkan!");
      navigate("/");
    } catch (error) {
      console.error("Error saat menyimpan data:", error);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen flex justify-center items-center">
      <form
        className="bg-white p-6 rounded shadow-md w-full max-w-md"
        onSubmit={saveData}
      >
        <h2 className="text-2xl font-bold mb-4">Tambah Data</h2>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Nama</label>
          <input
            type="text"
            className="input"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Nama"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Email</label>
          <input
            type="email"
            className="input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Nomor</label>
          <input
            type="text"
            className="input"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            placeholder="Nomor Kontak"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-green-500 hover:bg-green-600 text-white font-medium py-2 rounded"
        >
          Tambahkan
        </button>
      </form>
    </div>
  );
};

export default AddHome;
